import React, { useState } from "react";
import emailjs from "emailjs-com";
import toast from "react-hot-toast";

const ContactForm = () => {
  const [form, setForm] = useState({ name: "", email: "", phone: "", message: "" });

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    emailjs.send("service_id", "template_id", form, "public_key")
      .then(() => toast.success("Message sent!"))
      .catch(() => toast.error("Error sending message"));
  };

  return (
    <section className="py-16 px-6 bg-gray-100">
      <h2 className="text-3xl font-bold text-center mb-8">Contact Us</h2>
      <form onSubmit={handleSubmit} className="max-w-xl mx-auto bg-white shadow-lg rounded-2xl p-6">
        <input type="text" name="name" placeholder="Your Name" onChange={handleChange} className="w-full mb-4 p-3 border rounded-lg" />
        <input type="email" name="email" placeholder="Your Email" onChange={handleChange} className="w-full mb-4 p-3 border rounded-lg" />
        <input type="text" name="phone" placeholder="Your Phone" onChange={handleChange} className="w-full mb-4 p-3 border rounded-lg" />
        <textarea name="message" placeholder="Message" onChange={handleChange} className="w-full mb-4 p-3 border rounded-lg"></textarea>
        <button type="submit" className="w-full bg-purple-600 text-white py-3 rounded-lg">Send</button>
      </form>
    </section>
  );
};

export default ContactForm;
