// Features.jsx
import React, { useState, useEffect } from "react";
import "./Features.css";

const Features = () => {
  const [activeCard, setActiveCard] = useState(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Animation on component mount
    setIsVisible(true);
    
    // Add scroll event listener for parallax effect
    const handleScroll = () => {
      const elements = document.querySelectorAll('.features-card');
      elements.forEach(el => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.style.transform = `translateY(${rect.top * 0.05}px)`;
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const features = [
    {
      icon: "fas fa-file-alt",
      title: "Legal Templates",
      description: "Comprehensive collection of ready-to-use templates:",
      items: [
        "Petitions, Plaints, Written Statements",
        "Complaints, Counters, Arguments",
        "RTI (Tamil & English)",
        "Govt Applications, Contracts, MoUs",
        "Police Complaints, FIR, Matrimonial Docs"
      ],
      color: "#3498db"
    },
    {
      icon: "fas fa-gavel",
      title: "Auto-Drafting Engine",
      description: "AI-powered assistance for better drafting:",
      items: [
        "Clause-level Suggestions",
        "Risk Scoring",
        "Auto-fill Features",
        "Title Deed Opinion Drafts"
      ],
      color: "#e74c3c"
    },
    {
      icon: "fas fa-search",
      title: "OCR + Deep Search",
      description: "Smart scanning & interpretation:",
      items: [
        "Upload Tamil/English PDF/DOC",
        "Intelligent OCR Scanning",
        "Deep Search with Filters",
        "Legal Document Interpretation"
      ],
      color: "#2ecc71"
    }
  ];

  const handleCardClick = (index) => {
    if (activeCard === index) {
      setActiveCard(null);
    } else {
      setActiveCard(index);
    }
  };

  return (
    <section id="features" className={`features-section ${isVisible ? 'visible' : ''}`}>
      <div className="features-container">
        <h2 className="features-title">Platform Features</h2>
        <p className="features-subtitle">Powerful tools designed for legal professionals and individuals</p>
        
        <div className="features-grid">
          {features.map((feature, index) => (
            <div 
              key={index} 
              className={`features-card ${activeCard === index ? 'active' : ''}`}
              onClick={() => handleCardClick(index)}
            >
              <div className="features-card-inner">
                <div className="features-card-front">
                  <div className="features-icon-wrapper" style={{ borderColor: feature.color }}>
                    <div className="icon-circle" style={{ backgroundColor: feature.color }}>
                      <i className={feature.icon}></i>
                    </div>
                    <div className="icon-pulse" style={{ backgroundColor: feature.color }}></div>
                  </div>
                  <h3 className="features-card-title">{feature.title}</h3>
                  <p className="features-card-description">{feature.description}</p>
                  <div className="features-view-details">
                    <span>View Details</span>
                    <i className="fas fa-arrow-right"></i>
                  </div>
                </div>
                
                <div className="features-card-back" style={{ background: `linear-gradient(135deg, ${feature.color} 0%, #2c3e50 100%)` }}>
                  <div className="back-header">
                    <h3 className="features-card-title">{feature.title}</h3>
                    <div className="mini-icon">
                      <i className={feature.icon}></i>
                    </div>
                  </div>
                  <ul className="features-list">
                    {feature.items.map((item, i) => (
                      <li key={i}>
                        <i className="fas fa-check"></i>
                        {item}
                      </li>
                    ))}
                  </ul>
                  <button className="features-cta-button" style={{ backgroundColor: feature.color }}>
                    Learn More
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
        
        <div className="features-cta">
          <p>Ready to experience these features?</p>
          <button className="features-primary-button">
            Get Started Today
            <i className="fas fa-arrow-right"></i>
          </button>
        </div>
      </div>
    </section>
  );
};

export default Features;
