import React, { useState, useEffect } from 'react';
import './Audience.css';

const Audience = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Trigger animation when component mounts
    setIsVisible(true);
    
    // Add scroll event listener for reveal animation
    const handleScroll = () => {
      const element = document.getElementById('audience');
      if (element) {
        const position = element.getBoundingClientRect();
        if (position.top < window.innerHeight - 100) {
          setIsVisible(true);
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const audienceData = [
    {
      icon: 'fas fa-balance-scale',
      title: 'Advocates',
      description: 'Private workspace, case management, alternative arguments, e-filing, and client updates.'
    },
    {
      icon: 'fas fa-graduation-cap',
      title: 'Law Students',
      description: 'Dissertation help, moot court preparation, exam prep, and career guidance.'
    },
    {
      icon: 'fas fa-user-tie',
      title: 'Legal Clerks',
      description: 'Daily hearing calendars, SMS alerts, and fake case monitoring.'
    },
    {
      icon: 'fas fa-users',
      title: 'Parties',
      description: 'Case-based subscriptions with applied research and guidance.'
    },
    {
      icon: 'fas fa-search',
      title: 'Researchers',
      description: 'Dedicated doctrinal research tools and analysis frameworks.'
    },
    {
      icon: 'fas fa-child',
      title: 'General Public',
      description: 'Free legal queries, micropayments for extras, and easy-to-use templates.'
    }
  ];

  return (
    <section id="audience" className={`audience-section ${isVisible ? 'visible' : ''}`}>
      <div className="container">
        <h2 className="text-center section-title">Designed For Everyone</h2>
        <p className="text-center lead mb-5">
          Tailored experiences for different legal needs
        </p>

        <div className="row g-4">
          {audienceData.map((item, index) => (
            <div key={index} className="col-md-6 col-lg-4">
              <div className="user-type-card">
                <div className="user-icon">
                  <i className={item.icon}></i>
                </div>
                <h4>{item.title}</h4>
                <p>{item.description}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Audience;
