import React, { useState, useEffect, useRef } from "react";
import "./Stats.css";

const Stats = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [counts, setCounts] = useState([0, 0, 0, 0]);
  const [hasAnimated, setHasAnimated] = useState(false);
  const statsRef = useRef(null);
  
  const statsData = [
    { 
      number: 50, 
      suffix: "+", 
      label: "Integrated Features", 
      icon: "fas fa-cogs",
      color: "#3a86ff" // Custom color for each stat
    },
    { 
      number: 100, 
      suffix: "+", 
      label: "Years of Case Law", 
      icon: "fas fa-gavel",
      color: "#ff9e00"
    },
    { 
      number: 7, 
      suffix: "", 
      label: "User Categories", 
      icon: "fas fa-users",
      color: "#ff006e"
    },
    { 
      number: null, 
      suffix: "24/7", 
      label: "Legal Support", 
      icon: "fas fa-headset",
      color: "#8ac926"
    }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && !hasAnimated) {
          setIsVisible(true);
          setHasAnimated(true);
          
          // Animate the counters
          statsData.forEach((stat, index) => {
            if (stat.number !== null) {
              const duration = 2000; // ms
              const steps = 60;
              const stepValue = stat.number / steps;
              let currentStep = 0;
              
              const timer = setInterval(() => {
                currentStep += 1;
                setCounts(prev => {
                  const newCounts = [...prev];
                  newCounts[index] = Math.min(
                    Math.round(stepValue * currentStep), 
                    stat.number
                  );
                  return newCounts;
                });
                
                if (currentStep >= steps) clearInterval(timer);
              }, duration / steps);
            }
          });
        }
      },
      { threshold: 0.3, rootMargin: "0px 0px -100px 0px" }
    );

    if (statsRef.current) {
      observer.observe(statsRef.current);
    }

    return () => {
      if (statsRef.current) {
        observer.unobserve(statsRef.current);
      }
    };
  }, [hasAnimated]);

  return (
    <section className="stats-section" ref={statsRef}>
      <div className="stats-background">
        <div className="stats-grid-background"></div>
        <div className="stats-floating-shape shape-1"></div>
        <div className="stats-floating-shape shape-2"></div>
        <div className="stats-floating-shape shape-3"></div>
        <div className="stats-floating-shape shape-4"></div>
      </div>
      
      <div className="container">
        <div className="stats-header">
          <h2>Why LawMaster Stands Out</h2>
          <p>Trusted by legal professionals nationwide for excellence and reliability</p>
        </div>
        
        <div className="stats-grid">
          {statsData.map((stat, index) => (
            <div 
              key={index} 
              className={`stat-box ${isVisible ? 'visible' : ''}`}
              style={{ 
                transitionDelay: `${index * 0.1}s`,
                '--stat-color': stat.color // CSS variable for custom colors
              }}
            >
              <div className="stat-icon-wrapper">
                <div className="stat-icon-inner">
                  <i className={stat.icon}></i>
                </div>
                <div className="stat-icon-glow"></div>
                <svg className="stat-icon-circle" viewBox="0 0 100 100">
                  <circle cx="50" cy="50" r="48" />
                </svg>
              </div>
              
              <div className="stat-content">
                <div className="stat-number">
                  {stat.number !== null ? counts[index] + stat.suffix : stat.suffix}
                </div>
                <div className="stat-label">{stat.label}</div>
              </div>
              
              <div className="stat-hover-effect"></div>
              
              {/* Corner accents */}
              <div className="stat-corner-accent top-left"></div>
              <div className="stat-corner-accent top-right"></div>
              <div className="stat-corner-accent bottom-left"></div>
              <div className="stat-corner-accent bottom-right"></div>
            </div>
          ))}
        </div>
        
        <div className="stats-cta">
          <button className="stats-cta-button">
            Explore Our Features
            <span className="stats-cta-icon">
              <i className="fas fa-arrow-right"></i>
            </span>
          </button>
        </div>
      </div>
    </section>
  );
};

export default Stats;
