import React from "react";
import Lottie from "lottie-react";
import { motion } from "framer-motion";
import animationData from "../assets/hero.json"; // placeholder

const Hero = () => {
  return (
    <motion.section 
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.6 }}
      className="h-screen flex flex-col items-center justify-center bg-gradient-to-r from-purple-500 to-pink-500 text-white"
    >
      <div className="w-64 h-64">
        <Lottie animationData={animationData} loop={true} />
      </div>
      <h1 className="text-5xl font-bold mt-6">Welcome to PR Sparkz</h1>
      <p className="text-lg mt-4">Your Social Media Marketing Partner</p>
    </motion.section>
  );
};

export default Hero;
