import React, { useState, useEffect } from 'react';
import './Audience.css';

const Audience = () => {
  const [activeCard, setActiveCard] = useState(null);

  const audienceData = [
    {
      icon: 'fas fa-balance-scale',
      title: 'Advocates',
      description: 'Private workspace, case management, alternative arguments, e-filing, and client updates.',
      color: '#3B82F6'
    },
    {
      icon: 'fas fa-graduation-cap',
      title: 'Law Students',
      description: 'Dissertation help, moot court preparation, exam prep, and career guidance.',
      color: '#10B981'
    },
    {
      icon: 'fas fa-user-tie',
      title: 'Legal Clerks',
      description: 'Daily hearing calendars, SMS alerts, and fake case monitoring.',
      color: '#F59E0B'
    },
    {
      icon: 'fas fa-users',
      title: 'Parties',
      description: 'Case-based subscriptions with applied research and guidance.',
      color: '#EF4444'
    },
    {
      icon: 'fas fa-search',
      title: 'Researchers',
      description: 'Dedicated doctrinal research tools and analysis frameworks.',
      color: '#8B5CF6'
    },
    {
      icon: 'fas fa-child',
      title: 'General Public',
      description: 'Free legal queries, micropayments for extras, and easy-to-use templates.',
      color: '#EC4899'
    }
  ];

  return (
    <section id="audience" className="premium-audience">
      <div className="premium-container">
        <div className="premium-header">
          <h2 className="premium-title">Crafted For <span className="gradient-text">Every Legal Professional</span></h2>
          <p className="premium-subtitle">
            Experience tailored solutions designed specifically for your unique legal needs
          </p>
          <div className="title-accent"></div>
        </div>

        <div className="premium-grid">
          {audienceData.map((item, index) => (
            <div 
              key={index}
              className={`premium-card ${activeCard === index ? 'active' : ''}`}
              onMouseEnter={() => setActiveCard(index)}
              onMouseLeave={() => setActiveCard(null)}
              style={{ '--card-color': item.color }}
            >
              <div className="card-border-glow"></div>
              <div className="card-icon-container">
                <div className="icon-bg" style={{ backgroundColor: item.color }}></div>
                <i className={item.icon}></i>
              </div>
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              <div className="card-hover-content">
                <button className="premium-btn" style={{ backgroundColor: item.color }}>
                  Learn More
                  <span className="btn-arrow">â†’</span>
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Audience;
