import React, { useEffect, useState, useRef } from 'react';
import './Hero.css';

const Hero = () => {
  const [isVisible, setIsVisible] = useState(false);
  const heroRef = useRef(null);
  const canvasRef = useRef(null);

  useEffect(() => {
    // Trigger animations when component mounts
    setIsVisible(true);
    
    // Initialize particle animation
    const initParticles = () => {
      const canvas = canvasRef.current;
      if (!canvas) return;
      
      const ctx = canvas.getContext('2d');
      const particles = [];
      const particleCount = 80;
      
      // Set canvas size
      const resizeCanvas = () => {
        canvas.width = canvas.offsetWidth;
        canvas.height = canvas.offsetHeight;
      };
      
      resizeCanvas();
      
      // Create particles
      for (let i = 0; i < particleCount; i++) {
        particles.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          radius: Math.random() * 2 + 1,
          speedX: (Math.random() - 0.5) * 0.5,
          speedY: (Math.random() - 0.5) * 0.5,
          color: `rgba(255, 255, 255, ${Math.random() * 0.03 + 0.02})`
        });
      }
      
      // Animation loop
      const animate = () => {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        
        particles.forEach(particle => {
          // Update position
          particle.x += particle.speedX;
          particle.y += particle.speedY;
          
          // Wrap around edges
          if (particle.x < 0) particle.x = canvas.width;
          if (particle.x > canvas.width) particle.x = 0;
          if (particle.y < 0) particle.y = canvas.height;
          if (particle.y > canvas.height) particle.y = 0;
          
          // Draw particle
          ctx.beginPath();
          ctx.arc(particle.x, particle.y, particle.radius, 0, Math.PI * 2);
          ctx.fillStyle = particle.color;
          ctx.fill();
        });
        
        requestAnimationFrame(animate);
      };
      
      animate();
      
      // Handle resize
      window.addEventListener('resize', resizeCanvas);
      return () => window.removeEventListener('resize', resizeCanvas);
    };
    
    initParticles();
    
    // Animate stats counting with Intersection Observer
    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          animateCounters();
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.5 });
    
    if (heroRef.current) {
      observer.observe(heroRef.current);
    }

    return () => {
      if (heroRef.current) {
        observer.unobserve(heroRef.current);
      }
    };
  }, []);

  const animateCounters = () => {
    const counters = document.querySelectorAll('.stat-number');
    const speed = 2000;
    
    counters.forEach(counter => {
      const target = +counter.getAttribute('data-target');
      const count = +counter.innerText;
      const increment = target / speed;
      
      if (count < target) {
        counter.innerText = Math.ceil(count + increment);
        setTimeout(() => animateCounters(), 1);
      } else {
        counter.innerText = target.toLocaleString();
      }
    });
  };

  return (
    <section id="hero" className="hero" ref={heroRef}>
      <canvas ref={canvasRef} className="particles-canvas"></canvas>
      
      <div className="hero-background">
        <div className="background-grid"></div>
        <div className="background-shapes">
          <div className="shape shape-1"></div>
          <div className="shape shape-2"></div>
          <div className="shape shape-3"></div>
          <div className="shape shape-4"></div>
        </div>
      </div>
      
      <div className="hero-content">
        <div className={`hero-text ${isVisible ? 'visible' : ''}`}>
          <div className="pre-title">
            <span className="pre-title-line"></span>
            AI-Powered Legal Technology
          </div>
          <h1 className="hero-title">
            <span className="title-line">Transform Your Practice with</span>
            <span className="title-accent">LawMaster AI</span>
          </h1>
          <p className="hero-subtitle">
            Advanced artificial intelligence designed specifically for legal professionals. 
            Streamline research, drafting, and compliance with cutting-edge technology.
          </p>
          
          <div className="cta-container">
            <a href="#pricing" className="cta-button primary">
              <span>Start Free Trial</span>
              <div className="button-hover-effect"></div>
              <i className="fas fa-arrow-right"></i>
            </a>
            <a href="#demo" className="cta-button secondary">
              <i className="fas fa-play"></i>
              <span>Watch Demo</span>
            </a>
          </div>
          
          <div className="hero-highlights">
            <div className="highlight-item">
              <div className="highlight-icon">
                <i className="fas fa-check"></i>
              </div>
              <span>No credit card required</span>
            </div>
            <div className="highlight-item">
              <div className="highlight-icon">
                <i className="fas fa-check"></i>
              </div>
              <span>Set up in minutes</span>
            </div>
            <div className="highlight-item">
              <div className="highlight-icon">
                <i className="fas fa-check"></i>
              </div>
              <span>Free onboarding assistance</span>
            </div>
          </div>
        </div>
        
        <div className="hero-visual">
          <div className="visual-container">
            <div className="main-visual">
              <div className="dashboard-card">
                <div className="card-header">
                  <div className="card-header-icons">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                  <div className="card-title">Contract Analysis Dashboard</div>
                  <div className="card-actions">
                    <i className="fas fa-sync"></i>
                  </div>
                </div>
                <div className="card-content">
                  <div className="analysis-progress">
                    <div className="progress-bar">
                      <div className="progress-fill"></div>
                    </div>
                    <div className="progress-text">Analyzing document: 78% complete</div>
                  </div>
                  <div className="data-points">
                    <div className="data-point">
                      <div className="data-value">92%</div>
                      <div className="data-label">Accuracy</div>
                    </div>
                    <div className="data-point">
                      <div className="data-value">24</div>
                      <div className="data-label">Issues Found</div>
                    </div>
                    <div className="data-point">
                      <div className="data-value">5.2s</div>
                      <div className="data-label">Time Saved</div>
                    </div>
                  </div>
                  <div className="document-preview">
                    <div className="document-line"></div>
                    <div className="document-line"></div>
                    <div className="document-line highlighted">
                      <span className="highlight-tag">Clause 7.B - Review Recommended</span>
                    </div>
                    <div className="document-line"></div>
                    <div className="document-line"></div>
                  </div>
                </div>
              </div>
            </div>
            
            <div className="floating-element element-1">
              <div className="element-icon">
                <i className="fas fa-file-contract"></i>
              </div>
              <div className="element-text">Contract Review</div>
            </div>
            
            <div className="floating-element element-2">
              <div className="element-icon">
                <i className="fas fa-search"></i>
              </div>
              <div className="element-text">Legal Research</div>
            </div>
            
            <div className="floating-element element-3">
              <div className="element-icon">
                <i className="fas fa-chart-line"></i>
              </div>
              <div className="element-text">Case Analytics</div>
            </div>
          </div>
        </div>
      </div>
      
      <div className="stats-container">
        <div className="stat">
          <div className="stat-icon">
            <i className="fas fa-file-alt"></i>
          </div>
          <div className="stat-number" data-target="10000">0</div>
          <div className="stat-label">Legal Documents Processed</div>
        </div>
        
        <div className="stat">
          <div className="stat-icon">
            <i className="fas fa-target"></i>
          </div>
          <div className="stat-number" data-target="98">0</div>
          <div className="stat-label">Accuracy Rate (%)</div>
        </div>
        
        <div className="stat">
          <div className="stat-icon">
            <i className="fas fa-building"></i>
          </div>
          <div className="stat-number" data-target="500">0</div>
          <div className="stat-label">Law Firms Using</div>
        </div>
        
        <div className="stat">
          <div className="stat-icon">
            <i className="fas fa-clock"></i>
          </div>
          <div className="stat-number" data-target="24">0</div>
          <div className="stat-label">Hours Saved Weekly</div>
        </div>
      </div>
      
      <div className="scroll-indicator">
        <div className="scroll-arrow">
          <span></span>
        </div>
        <div className="scroll-text">Scroll to discover</div>
      </div>
    </section>
  );
};

export default Hero;
