import React, { useState, useEffect, useRef } from "react";
import "./Stats.css";

const Stats = () => {
  const [isVisible, setIsVisible] = useState(false);
  const statsRef = useRef(null);
  
  const statsData = [
    { number: 10000, label: "Legal Documents Processed" },
    { number: 98, suffix: "%", label: "Accuracy Rate" },
    { number: 500, label: "Law Firms Using" },
    { number: 24, label: "Hours Saved Weekly" }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.3 }
    );

    if (statsRef.current) {
      observer.observe(statsRef.current);
    }

    return () => {
      if (statsRef.current) {
        observer.unobserve(statsRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (!isVisible) return;

    const statNumbers = document.querySelectorAll('.stat-number');
    
    statNumbers.forEach((element) => {
      const target = parseInt(element.getAttribute('data-target'));
      const suffix = element.textContent.includes('%') ? '%' : '';
      const duration = 2000; // ms
      const steps = 60;
      const stepValue = target / steps;
      let currentStep = 0;
      
      const timer = setInterval(() => {
        currentStep += 1;
        const currentValue = Math.min(Math.round(stepValue * currentStep), target);
        element.textContent = currentValue + suffix;
        
        if (currentStep >= steps) clearInterval(timer);
      }, duration / steps);
    });
  }, [isVisible]);

  return (
    <section className="stats-section" ref={statsRef}>
      <div className="container">
        <div className="stats-header">
          <h2>Trusted by Legal Professionals Nationwide</h2>
          <p>LawMaster delivers proven results for law firms of all sizes</p>
        </div>
        
        <div className="stats-container">
          {statsData.map((stat, index) => (
            <React.Fragment key={index}>
              <div className="stat">
                <div 
                  className="stat-number" 
                  data-target={stat.number}
                >
                  {stat.suffix ? `0${stat.suffix}` : '0'}
                </div>
                <div className="stat-label">{stat.label}</div>
              </div>
              {index < statsData.length - 1 && <div className="stat-divider"></div>}
            </React.Fragment>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Stats;
