import React, { useState, useEffect, useRef } from "react";
import "./Stats.css";

const Stats = () => {
  const [isVisible, setIsVisible] = useState(false);
  const statsRef = useRef(null);
  
  const statsData = [
    { number: 10000, label: "Legal Documents Processed", suffix: "+" },
    { number: 98, label: "Accuracy Rate", suffix: "%" },
    { number: 500, label: "Law Firms Using", suffix: "+" },
    { number: 24, label: "Hours Saved Weekly", prefix: "~" },
    { number: 99, label: "Client Satisfaction", suffix: "%" },
    { number: 5, label: "Minutes Average Processing Time", suffix: "" },
    { number: 2500, label: "Cases Successfully Closed", suffix: "+" },
    { number: 4, label: "Continents Served", suffix: "" }
  ];

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
        }
      },
      { threshold: 0.3 }
    );

    if (statsRef.current) {
      observer.observe(statsRef.current);
    }

    return () => {
      if (statsRef.current) {
        observer.unobserve(statsRef.current);
      }
    };
  }, []);

  useEffect(() => {
    if (!isVisible) return;

    const statNumbers = document.querySelectorAll('.stat-number');
    
    statNumbers.forEach((element) => {
      const target = parseInt(element.getAttribute('data-target'));
      const prefix = element.getAttribute('data-prefix') || '';
      const suffix = element.getAttribute('data-suffix') || '';
      const duration = 2000; // ms
      const steps = 60;
      const stepValue = target / steps;
      let currentStep = 0;
      
      const timer = setInterval(() => {
        currentStep += 1;
        const currentValue = Math.min(Math.round(stepValue * currentStep), target);
        element.textContent = prefix + currentValue.toLocaleString() + suffix;
        
        if (currentStep >= steps) clearInterval(timer);
      }, duration / steps);
    });
  }, [isVisible]);

  return (
    <section className="stats-section" ref={statsRef}>
      <div className="container">
        <div className="stats-header">
          <h2>Trusted by Legal Professionals Nationwide</h2>
          <p>LawMaster delivers proven results for law firms of all sizes</p>
        </div>
        
        <div className="stats-grid">
          {statsData.map((stat, index) => (
            <div key={index} className="stat-item">
              <div 
                className="stat-number" 
                data-target={stat.number}
                data-prefix={stat.prefix || ''}
                data-suffix={stat.suffix || ''}
              >
                {stat.prefix || ''}0{stat.suffix || ''}
              </div>
              <div className="stat-label">{stat.label}</div>
            </div>
          ))}
        </div>
        
        <div className="stats-cta">
          <button className="cta-button">See Case Studies</button>
        </div>
      </div>
    </section>
  );
};

export default Stats;
