import React, { useState, useEffect } from 'react';
import './Testimonials.css';

const Testimonials = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  
  const testimonials = [
    {
      name: "Rajesh Kumar",
      role: "Advocate, Chennai",
      content: "The auto-drafting engine has cut my document preparation time by 70%. The risk scoring feature is incredibly accurate.",
      rating: 5
    },
    {
      name: "Priya S.",
      role: "Law Student, Delhi",
      content: "The courtroom simulation helped me prepare for my moot court competition. I felt confident and well-prepared!",
      rating: 4
    },
    {
      name: "Vikram Mehta",
      role: "Business Owner, Mumbai",
      content: "As someone without legal background, LawMaster helped me understand and create contracts that protect my business interests.",
      rating: 5
    },
    {
      name: "Anjali Patel",
      role: "Legal Consultant, Bangalore",
      content: "The document analysis feature has been a game-changer for my practice. It saves hours of manual review time.",
      rating: 5
    },
    {
      name: "Sanjay R.",
      role: "Corporate Lawyer, Hyderabad",
      content: "The AI-powered legal research tools have significantly improved my case preparation efficiency.",
      rating: 4
    }
  ];

  // Auto-rotate testimonials
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prevIndex) => (prevIndex + 1) % testimonials.length);
    }, 5000);
    
    return () => clearInterval(interval);
  }, [testimonials.length]);

  const handleDotClick = (index) => {
    setActiveIndex(index);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span 
        key={index} 
        className={`star ${index < rating ? 'filled' : ''}`}
      >★</span>
    ));
  };

  return (
    <section className="testimonials-section">
      <div className="container">
        <h2 className="section-title">What Our Users Say</h2>
        <p className="section-subtitle">Discover how LawMaster is transforming legal practice across India</p>
        
        <div className="testimonials-container">
          <div className="testimonials-track" style={{ transform: `translateX(-${activeIndex * 100}%)` }}>
            {testimonials.map((testimonial, index) => (
              <div key={index} className="testimonial-card">
                <div className="testimonial-content">
                  <div className="quote-icon">"</div>
                  <p>{testimonial.content}</p>
                </div>
                <div className="testimonial-author">
                  <div className="author-avatar">
                    <i className="fas fa-user-circle"></i>
                  </div>
                  <div className="author-details">
                    <h4>{testimonial.name}</h4>
                    <p>{testimonial.role}</p>
                    <div className="rating">
                      {renderStars(testimonial.rating)}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        
        <div className="testimonials-controls">
          {testimonials.map((_, index) => (
            <button
              key={index}
              className={`control-dot ${index === activeIndex ? 'active' : ''}`}
              onClick={() => handleDotClick(index)}
              aria-label={`View testimonial ${index + 1}`}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
