import React, { useEffect } from "react";
import AOS from "aos";
import "aos/dist/aos.css";
import { FaBullhorn, FaChartLine, FaUsers } from "react-icons/fa";

const Services = () => {
  useEffect(() => {
    AOS.init({ duration: 800 });
  }, []);

  const items = [
    { icon: <FaBullhorn />, title: "Brand Awareness", desc: "Boost your brand's reach" },
    { icon: <FaChartLine />, title: "Analytics", desc: "Track performance with insights" },
    { icon: <FaUsers />, title: "Community Growth", desc: "Engage with your audience" }
  ];

  return (
    <section className="py-16 bg-gray-50 text-center">
      <h2 className="text-3xl font-bold mb-12">Our Services</h2>
      <div className="grid md:grid-cols-3 gap-8 px-6">
        {items.map((s, idx) => (
          <div key={idx} className="bg-white shadow-lg rounded-2xl p-6" data-aos="fade-up">
            <div className="text-4xl text-purple-600 mb-4">{s.icon}</div>
            <h3 className="text-xl font-semibold mb-2">{s.title}</h3>
            <p>{s.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Services;
