// components/Stats.js
import React, { useState, useEffect, useRef } from "react";
import "./Stats.css";

const Stats = () => {
  const [animated, setAnimated] = useState(false);
  const statsRef = useRef(null);
  const [visibleStats, setVisibleStats] = useState([false, false, false, false]);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setAnimated(true);
          
          // Animate each stat with a delay
          visibleStats.forEach((_, index) => {
            setTimeout(() => {
              setVisibleStats(prev => {
                const newState = [...prev];
                newState[index] = true;
                return newState;
              });
            }, index * 300);
          });
        }
      },
      { threshold: 0.4 }
    );

    if (statsRef.current) {
      observer.observe(statsRef.current);
    }

    return () => {
      if (statsRef.current) {
        observer.unobserve(statsRef.current);
      }
    };
  }, []);

  return (
    <section className="stats-section" ref={statsRef}>
      <div className="stats-background">
        <div className="stats-floating-shape shape-1"></div>
        <div className="stats-floating-shape shape-2"></div>
        <div className="stats-floating-shape shape-3"></div>
        <div className="stats-floating-shape shape-4"></div>
      </div>
      
      <div className="container">
        <div className="stats-header">
          <h2>Why LawMaster Stands Out</h2>
          <p>Trusted by legal professionals nationwide</p>
        </div>
        
        <div className="stats-grid">
          <div className={`stat-box ${visibleStats[0] ? 'visible' : ''}`}>
            <div className="stat-icon-wrapper">
              <div className="stat-icon-inner">
                <i className="fas fa-cogs"></i>
              </div>
              <svg className="stat-icon-circle" viewBox="0 0 100 100">
                <circle cx="50" cy="50" r="48" />
              </svg>
            </div>
            <div className="stat-content">
              <div className="stat-number" data-target="50">
                {visibleStats[0] ? "50+" : "0"}
              </div>
              <div className="stat-label">Integrated Features</div>
            </div>
            <div className="stat-hover-effect"></div>
          </div>

          <div className={`stat-box ${visibleStats[1] ? 'visible' : ''}`}>
            <div className="stat-icon-wrapper">
              <div className="stat-icon-inner">
                <i className="fas fa-gavel"></i>
              </div>
              <svg className="stat-icon-circle" viewBox="0 0 100 100">
                <circle cx="50" cy="50" r="48" />
              </svg>
            </div>
            <div className="stat-content">
              <div className="stat-number" data-target="100">
                {visibleStats[1] ? "100+" : "0"}
              </div>
              <div className="stat-label">Years of Case Law</div>
            </div>
            <div className="stat-hover-effect"></div>
          </div>

          <div className={`stat-box ${visibleStats[2] ? 'visible' : ''}`}>
            <div className="stat-icon-wrapper">
              <div className="stat-icon-inner">
                <i className="fas fa-users"></i>
              </div>
              <svg className="stat-icon-circle" viewBox="0 0 100 100">
                <circle cx="50" cy="50" r="48" />
              </svg>
            </div>
            <div className="stat-content">
              <div className="stat-number" data-target="7">
                {visibleStats[2] ? "7" : "0"}
              </div>
              <div className="stat-label">User Categories</div>
            </div>
            <div className="stat-hover-effect"></div>
          </div>

          <div className={`stat-box ${visibleStats[3] ? 'visible' : ''}`}>
            <div className="stat-icon-wrapper">
              <div className="stat-icon-inner">
                <i className="fas fa-headset"></i>
              </div>
              <svg className="stat-icon-circle" viewBox="0 0 100 100">
                <circle cx="50" cy="50" r="48" />
              </svg>
            </div>
            <div className="stat-content">
              <div className="stat-number">24/7</div>
              <div className="stat-label">Legal Support</div>
            </div>
            <div className="stat-hover-effect"></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Stats;
