import React, { useState, useEffect } from 'react';
import './Testimonials.css';

const Testimonials = () => {
  const [flippedCard, setFlippedCard] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  
  const testimonials = [
    {
      id: 1,
      name: "Rajesh Kumar",
      role: "Advocate, Chennai",
      content: "The auto-drafting engine has cut my document preparation time by 70%. The risk scoring feature is incredibly accurate.",
      detailed: "I've been using LawMaster for 6 months now and it has completely transformed my practice. The document automation saves me hours each week, and the AI-powered risk assessment has helped me avoid several potential issues with contracts.",
      rating: 5,
      category: "professional"
    },
    {
      id: 2,
      name: "Priya S.",
      role: "Law Student, Delhi",
      content: "The courtroom simulation helped me prepare for my moot court competition. I felt confident and well-prepared!",
      detailed: "As a law student, I found the courtroom simulation feature incredibly valuable. It allowed me to practice arguments and get feedback on my presentation skills. The AI judge provided surprisingly accurate assessments of my performance.",
      rating: 4,
      category: "education"
    },
    {
      id: 3,
      name: "Vikram Mehta",
      role: "Business Owner, Mumbai",
      content: "As someone without legal background, LawMaster helped me understand and create contracts that protect my business interests.",
      detailed: "Running a small business means I need to handle legal documents but can't afford a full-time lawyer. LawMaster's guided contract creation and plain-English explanations have been invaluable. The templates are comprehensive and easy to customize.",
      rating: 5,
      category: "business"
    },
    {
      id: 4,
      name: "Anjali Patel",
      role: "Legal Consultant, Bangalore",
      content: "The document analysis feature has been a game-changer for my practice. It saves hours of manual review time.",
      detailed: "As a legal consultant, I review countless contracts and legal documents. LawMaster's analysis tools highlight potential issues and inconsistencies that I might have missed. The comparison feature is particularly useful for tracking changes across document versions.",
      rating: 5,
      category: "professional"
    },
    {
      id: 5,
      name: "Sanjay R.",
      role: "Corporate Lawyer, Hyderabad",
      content: "The AI-powered legal research tools have significantly improved my case preparation efficiency.",
      detailed: "The research feature quickly finds relevant case law and statutes, saving me countless hours in the library. The citation checker ensures all my references are accurate and up-to-date, which is crucial for court submissions.",
      rating: 4,
      category: "professional"
    },
    {
      id: 6,
      name: "Neha Gupta",
      role: "Startup Founder, Pune",
      content: "LawMaster helped us navigate complex compliance requirements during our funding round with ease.",
      detailed: "As a first-time founder, the legal aspects of fundraising were daunting. LawMaster's guided workflows and compliance checklists ensured we didn't miss any critical steps. The investor document templates were professional and comprehensive.",
      rating: 5,
      category: "business"
    }
  ];

  const [visibleCards, setVisibleCards] = useState(3);
  const [animatedCards, setAnimatedCards] = useState([]);

  useEffect(() => {
    // Animate cards one by one on component mount
    const timer = setTimeout(() => {
      const animationInterval = setInterval(() => {
        setAnimatedCards(prev => {
          if (prev.length < testimonials.length) {
            return [...prev, testimonials[prev.length].id];
          }
          clearInterval(animationInterval);
          return prev;
        });
      }, 200);
    }, 500);

    return () => {
      clearTimeout(timer);
    };
  }, [testimonials.length]);

  const handleCardClick = (id) => {
    if (flippedCard === id) {
      setFlippedCard(null);
    } else {
      setFlippedCard(id);
    }
  };

  const filteredTestimonials = activeFilter === 'all' 
    ? testimonials 
    : testimonials.filter(t => t.category === activeFilter);

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, index) => (
      <span 
        key={index} 
        className={`star ${index < rating ? 'filled' : ''}`}
      >★</span>
    ));
  };

  const showMore = () => {
    setVisibleCards(prev => Math.min(prev + 3, testimonials.length));
  };

  const showLess = () => {
    setVisibleCards(3);
  };

  return (
    <section className="testimonials-section">
      <div className="testimonials-background">
        <div className="floating-shape shape-1"></div>
        <div className="floating-shape shape-2"></div>
        <div className="floating-shape shape-3"></div>
        <div className="floating-shape shape-4"></div>
      </div>
      
      <div className="container">
        <h2 className="section-title">What Our Users Say</h2>
        <p className="section-subtitle">Discover how LawMaster is transforming legal practice across India</p>
        
        <div className="filter-buttons">
          <button 
            className={activeFilter === 'all' ? 'active' : ''}
            onClick={() => setActiveFilter('all')}
          >
            All Testimonials
          </button>
          <button 
            className={activeFilter === 'professional' ? 'active' : ''}
            onClick={() => setActiveFilter('professional')}
          >
            Legal Professionals
          </button>
          <button 
            className={activeFilter === 'business' ? 'active' : ''}
            onClick={() => setActiveFilter('business')}
          >
            Business Owners
          </button>
          <button 
            className={activeFilter === 'education' ? 'active' : ''}
            onClick={() => setActiveFilter('education')}
          >
            Students & Education
          </button>
        </div>
        
        <div className="testimonials-grid">
          {filteredTestimonials.slice(0, visibleCards).map((testimonial, index) => (
            <div 
              key={testimonial.id} 
              className={`testimonial-card ${flippedCard === testimonial.id ? 'flipped' : ''} ${animatedCards.includes(testimonial.id) ? 'animate-in' : ''}`}
              style={{ animationDelay: `${index * 0.1}s` }}
              onClick={() => handleCardClick(testimonial.id)}
            >
              <div className="card-inner">
                <div className="card-front">
                  <div className="card-content">
                    <div className="quote-icon">"</div>
                    <p className="testimonial-text">{testimonial.content}</p>
                    <div className="testimonial-author">
                      <div className="author-avatar">
                        <i className="fas fa-user-circle"></i>
                      </div>
                      <div className="author-details">
                        <h4>{testimonial.name}</h4>
                        <p>{testimonial.role}</p>
                        <div className="rating">
                          {renderStars(testimonial.rating)}
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="card-flip-hint">
                    <i className="fas fa-sync-alt"></i> Click for more details
                  </div>
                </div>
                <div className="card-back">
                  <div className="card-content">
                    <h3>Detailed Experience</h3>
                    <p className="testimonial-detailed">{testimonial.detailed}</p>
                    <div className="testimonial-author">
                      <div className="author-details">
                        <h4>{testimonial.name}</h4>
                        <p>{testimonial.role}</p>
                        <div className="rating">
                          {renderStars(testimonial.rating)}
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="card-flip-hint">
                    <i className="fas fa-sync-alt"></i> Click to return
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
        
        <div className="testimonials-controls">
          {visibleCards < filteredTestimonials.length && (
            <button className="show-more-btn" onClick={showMore}>
              Show More Testimonials
            </button>
          )}
          {visibleCards > 3 && (
            <button className="show-less-btn" onClick={showLess}>
              Show Less
            </button>
          )}
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
