import React, { useState, useEffect, useRef } from 'react';
import './Audience.css';

const Audience = () => {
  const [activeCard, setActiveCard] = useState(null);
  const [visibleCards, setVisibleCards] = useState([]);
  const sectionRef = useRef(null);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const timer = setTimeout(() => {
              setVisibleCards([0, 1, 2, 3, 4, 5]);
            }, 300);
            return () => clearTimeout(timer);
          }
        });
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const audienceData = [
    {
      icon: 'fas fa-balance-scale',
      title: 'Advocates',
      description: 'Private workspace, case management, alternative arguments, e-filing, and client updates.',
      features: ['Case Management', 'E-Filing', 'Client Portal', 'Document Automation'],
      stats: { satisfaction: 98, support: 24 },
      color: '#2563EB'
    },
    {
      icon: 'fas fa-graduation-cap',
      title: 'Law Students',
      description: 'Dissertation help, moot court preparation, exam prep, and career guidance.',
      features: ['Exam Prep', 'Moot Court', 'Career Guidance', 'Legal Research'],
      stats: { satisfaction: 95, support: 18 },
      color: '#059669'
    },
    {
      icon: 'fas fa-user-tie',
      title: 'Legal Clerks',
      description: 'Daily hearing calendars, SMS alerts, and fake case monitoring.',
      features: ['Hearing Calendar', 'SMS Alerts', 'Case Monitoring', 'Document Drafting'],
      stats: { satisfaction: 97, support: 24 },
      color: '#D97706'
    },
    {
      icon: 'fas fa-users',
      title: 'Parties',
      description: 'Case-based subscriptions with applied research and guidance.',
      features: ['Case Tracking', 'Research', 'Guidance', 'Status Updates'],
      stats: { satisfaction: 92, support: 12 },
      color: '#DC2626'
    },
    {
      icon: 'fas fa-search',
      title: 'Researchers',
      description: 'Dedicated doctrinal research tools and analysis frameworks.',
      features: ['Research Tools', 'Analysis', 'Frameworks', 'Data Visualization'],
      stats: { satisfaction: 96, support: 24 },
      color: '#7C3AED'
    },
    {
      icon: 'fas fa-child',
      title: 'General Public',
      description: 'Free legal queries, micropayments for extras, and easy-to-use templates.',
      features: ['Free Queries', 'Templates', 'Micropayments', 'Document Library'],
      stats: { satisfaction: 90, support: 16 },
      color: '#DB2777'
    }
  ];

  return (
    <section id="audience" className="ultimate-audience" ref={sectionRef}>
      {/* Animated background elements */}
      <div className="floating-particles">
        {[...Array(15)].map((_, i) => (
          <div key={i} className="particle" style={{
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
            animationDelay: `${Math.random() * 5}s`,
            animationDuration: `${15 + Math.random() * 10}s`
          }}></div>
        ))}
      </div>
      
      <div className="cursor-follower" style={{
        left: mousePosition.x,
        top: mousePosition.y,
        opacity: activeCard !== null ? 0.2 : 0
      }}></div>
      
      <div className="ultimate-container">
        <div className="ultimate-header">
          <div className="header-badge">
            <span>Innovative Legal Solutions</span>
          </div>
          <h2 className="ultimate-title">
            <span className="title-word title-word-1">Designed</span>
            <span className="title-word title-word-2">For</span>
            <span className="title-word title-word-3">Every</span>
            <span className="title-word title-word-4">Legal</span>
            <span className="title-word title-word-5">Professional</span>
          </h2>
          <p className="ultimate-subtitle">
            Experience tailored solutions designed specifically for your unique legal needs and requirements
          </p>
          <div className="title-accent">
            <div className="accent-dot"></div>
            <div className="accent-line"></div>
            <div className="accent-dot"></div>
          </div>
        </div>

        <div className="ultimate-grid">
          {audienceData.map((item, index) => (
            <div 
              key={index}
              className={`ultimate-card ${activeCard === index ? 'active' : ''} ${visibleCards.includes(index) ? 'visible' : ''}`}
              onMouseEnter={() => setActiveCard(index)}
              onMouseLeave={() => setActiveCard(null)}
              style={{ '--card-color': item.color }}
            >
              <div className="card-border-glow"></div>
              <div className="card-shine"></div>
              
              <div className="card-header">
                <div className="card-icon-container">
                  <div className="icon-bg" style={{ backgroundColor: item.color }}></div>
                  <div className="icon-inner-circle"></div>
                  <i className={item.icon}></i>
                </div>
                <div className="card-badge">Premium</div>
              </div>
              
              <h3>{item.title}</h3>
              <p>{item.description}</p>
              
              <div className="card-features">
                {item.features.map((feature, i) => (
                  <div key={i} className="feature-item">
                    <div className="feature-icon">
                      <i className="fas fa-check"></i>
                    </div>
                    <span>{feature}</span>
                  </div>
                ))}
              </div>
              
              <div className="card-hover-content">
                <button className="ultimate-btn" style={{ backgroundColor: item.color }}>
                  <span>Explore Features</span>
                  <div className="btn-icon">
                    <i className="fas fa-arrow-right"></i>
                  </div>
                </button>
                
                <div className="card-stats">
                  <div className="stat">
                    <div className="stat-number">{item.stats.satisfaction}%</div>
                    <div className="stat-label">Satisfaction</div>
                  </div>
                  <div className="divider"></div>
                  <div className="stat">
                    <div className="stat-number">{item.stats.support}/7</div>
                    <div className="stat-label">Support</div>
                  </div>
                </div>
              </div>
              
              <div className="card-decoration">
                <div className="decoration-item"></div>
                <div className="decoration-item"></div>
                <div className="decoration-item"></div>
              </div>
            </div>
          ))}
        </div>
        
        <div className="ultimate-cta">
          <div className="cta-content">
            <h3>Ready to transform your legal practice?</h3>
            <p>Join thousands of legal professionals who trust our platform</p>
            <div className="cta-buttons">
              <button className="cta-button primary">
                <i className="fas fa-rocket"></i>
                Get Started
              </button>
              <button className="cta-button secondary">
                <i className="fas fa-comments"></i>
                Contact Sales
              </button>
            </div>
          </div>
          <div className="cta-graphic">
            <div className="graphic-circle"></div>
            <div className="graphic-item"></div>
            <div className="graphic-item"></div>
            <div className="graphic-item"></div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Audience;
