import React, { useState, useEffect } from "react";
import "./Audience.css";

const cardData = [
  { id: 1, title: "Lawyers", desc: "Tools and resources to draft, review, and manage legal docs." },
  { id: 2, title: "Students", desc: "Learn law faster with guides, case studies, and mock tests." },
  { id: 3, title: "Businesses", desc: "Contracts, compliance checklists, and dispute management." },
  { id: 4, title: "Citizens", desc: "Simple explanations of legal rights and government forms." },
  { id: 5, title: "Researchers", desc: "Access legal data, precedents, and advanced search tools." },
  { id: 6, title: "Courts", desc: "Digital filing, case management, and AI-powered assistance." },
];

const Audience = () => {
  const [visibleCards, setVisibleCards] = useState([]);

  useEffect(() => {
    const timers = [];
    cardData.forEach((_, index) => {
      timers.push(
        setTimeout(() => {
          setVisibleCards((prev) => [...prev, index]);
        }, index * 300) // delay each card by 300ms
      );
    });
    return () => timers.forEach((t) => clearTimeout(t));
  }, []);

  return (
    <section className="audience">
      <h2 className="audience-title">Who Can Use This?</h2>
      <div className="audience-grid">
        {cardData.map((card, index) => (
          <div
            key={card.id}
            className={`audience-card ${visibleCards.includes(index) ? "visible" : ""}`}
          >
            <h3>{card.title}</h3>
            <p>{card.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Audience;
